import java.util.Scanner;

public class CalculateSAL {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int total=1;
		Scanner scanner = new Scanner (System.in);
		System.out.println("enter salary : ");
		int salary= scanner.nextInt();
		if 
		(salary<8000);
		//{System.out.println(salary/2);}
		
		else  System.out.println(" salary is too large  ");
		
		System.out.println("enter number of shifts : ");
		int shifts= scanner.nextInt();	
	  if(shifts>0);
	
	total=(salary/2)+(salary*(2*(shifts))/100);
	System.out.println("Total salary is :"+total);
	
	  //else  System.out.println(" shifts too small  ");
 
	}

}
